package com.loafer.core.utils;

import org.apache.commons.codec.binary.Base64;

import java.io.FileOutputStream;
import java.io.OutputStream;
import java.nio.file.Files;
import java.nio.file.Paths;

public class Base64FileConvert {

    public static void main(String[] args) {
        String base64 = "";
//        String base64 = "";
        boolean success = generateBase64StringToFile(base64, "C:\\Users\\18238\\Desktop\\base64ToFile.png");
    }

    /**
     * 对字节数组字符串进行Base64解码并生成文件
     * @param fileStr 文件base64位数据
     * @param fileFilePath 保存文件全路径地址
     * @return
     */
    public static boolean generateBase64StringToFile(String fileStr,String fileFilePath){
        if (fileStr == null) //文件base64位数据为空
            return false;
        try
        {
            //Base64解码
            byte[] b = Base64.decodeBase64(fileStr);
            for(int i=0;i<b.length;++i)
            {
                if(b[i]<0)
                {//调整异常数据
                    b[i]+=256;
                }
            }
            //生成文件
            OutputStream out = Files.newOutputStream(Paths.get(fileFilePath));
            out.write(b);
            out.flush();
            out.close();
            return true;
        }
        catch (Exception e)
        {
            return false;
        }
    }

}
